CREATE TABLE `cfg_outline` (  `id` bigint(20) NOT NULL AUTO_INCREMENT,  `pid` bigint(20) NOT NULL DEFAULT '0' COMMENT '父级概要',  `name` varchar(200) NOT NULL DEFAULT '' COMMENT '概要名称',  `olevel` tinyint(4) NOT NULL DEFAULT '1' COMMENT '概要层级',  `is_leaf` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否为末级概要',  `opath` varchar(100) NOT NULL DEFAULT '' COMMENT '概要路径',  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '概要状态,控制是否展示--1启用/0停用',  `business_type` int(11) NOT NULL DEFAULT '10' COMMENT '业务类型 10 用户纠纷 11 商家纠纷',  `is_delete` tinyint(4) NOT NULL DEFAULT '0',  PRIMARY KEY (`id`),  KEY `idx_pid` (`pid`) USING BTREE,  KEY `idx_opath` (`opath`) USING BTREE) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
